import base64
import threading
from tkinter import *
from PIL import Image
from io import BytesIO
from pyautogui import *
from pygetwindow import *
import keyboard

target_image_base64 = ""
image_binary_data = base64.b64decode(target_image_base64)
target_image_path = "temp_image.png"  # Путь к временному файлу

# Сохраняем бинарные данные в файл
with open(target_image_path, "wb") as file:
    file.write(image_binary_data)

skr_active = False
root = Tk()
toggle_button_var = IntVar()


def run_script():
    active_window = getActiveWindow()
    if active_window:
        try:
            location = locateCenterOnScreen(target_image_path, confidence=0.6)
            if location:
                center_x, center_y = location
                print(f'Найдено на: {center_x}, {center_y}')
                sleep(0.5)
                mouseDown(button='left', x=center_x, y=center_y)
                sleep(0.1)
                mouseUp(button='left')  # Клик на найденую картинку
            else:
                print('Картинка не найдена')
        except ImageNotFoundException:
            print('Не нашел. Ищу снова...')
            sleep(1)


def run_script_thread():
    while skr_active:
        run_script()
        sleep(0.1)


def toggle_skr():
    global skr_active
    skr_active = not skr_active
    toggle_button_var.set(1 if skr_active else 0)
    print(f"Акто-ацепт {'ВКЛ' if skr_active else 'выключен'}")
    if skr_active:
        threading.Thread(target=run_script_thread).start()


def on_chbx_change():
    toggle_skr()


toggle_button = Checkbutton(root, text='Включить/Выключить', variable=toggle_button_var, command=on_chbx_change)
toggle_button.pack(pady=10)  # Создание флажка вкл/выкл
keyboard.add_hotkey('F10', lambda: toggle_skr())  # Хоткей на вкл/выкл


def on_closing():
    global skr_active
    skr_active = False
    root.destroy()


root.protocol("WM_DELETE_WINDOW", on_closing)  # Обработчик закрытия окна

root.mainloop()
